mapper.select:
select 

from X_DOCUMENTOS_TBL DOCU
where UID_DOCUMENTO = #{uidDocumento,jdbcType=VARCHAR}

mapper.update:
update X_DOCUMENTOS_TBL
  set ESTADO = #{estado,jdbcType=CHAR}
where UID_DOCUMENTO = #{uidDocumento,jdbcType=VARCHAR}

mapper.select:
select
distinct
from X_DOCUMENTOS_TBL DOCU join X_BONOS_TBL BONO ON (DOCU.CODALM = BONO.CODALM AND DOCU.ID_DOCUMENTO = TO_CHAR(BONO.ID_BONO))
order by ${orderByClause}

select
distinct
from X_DOCUMENTOS_TBL DOCU join X_LOG_GIFTCARD_TBL GIF ON (DOCU.REFERENCIA = GIF.ID_GIFTCARD AND DOCU.CODALM = GIF.COD_ALM AND DOCU.CODCAJA = GIF.COD_CAJA AND DOCU.ID_DOCUMENTO = GIF.ID_CARGA_GIFTCARD)
order by ${orderByClause}

select
distinct
from X_DOCUMENTOS_TBL DOCU join X_NOTAS_CREDITO_TBL NOTA ON (DOCU.CODALM = NOTA.CODALM AND DOCU.CODCAJA = NOTA.CODCAJA AND DOCU.ID_DOCUMENTO = TO_CHAR(NOTA.ID_NOTA_CREDITO))
order by ${orderByClause}

select
distinct
from X_DOCUMENTOS_TBL DOCU join X_RESERVA_TBL RES ON (DOCU.CODALM = RES.CODALM AND DOCU.ID_DOCUMENTO = TO_CHAR(RES.COD_RESERVACION))
order by ${orderByClause}
